  selector: 'app-interval',
  templateUrl: './interval.component.html',
  styleUrls: ['./interval.component.scss']
})
export class IntervalComponent {
  count: Number = 0;
   subsc:Subscription; 
  constructor(){

const intervalVideos = interval(2000);
 this.subsc = intervalVideos.subscribe(x => {
  console.log(x);
  this.count=x;
  if(x==5){
